arguments.argument:
//automatically change maps if the server is empty for too long
//if (global.isHost and hostTimer > 0 ) //and  //(!(!KothRedControlPoint.locked and KothRedControlPoint.capping == 0 and KothRedControlPoint.bluePresence == 0 and KothRedControlPoint.redPresence == 0) or !(!KothBlueControlPoint.locked and KothBlueControlPoint.capping == 0 and KothBlueControlPoint.bluePresence == 0 and KothBlueControlPoint.redPresence == 0))
if (global.isHost and hostTimer > 0 and 
   ((!KothRedControlPoint.locked and KothRedControlPoint.capping == 0 and KothRedControlPoint.bluePresence == 0) or
   (!KothBlueControlPoint.locked and KothBlueControlPoint.capping == 0 and KothBlueControlPoint.redPresence == 0))
   and redTimer + blueTimer == 10800)
{
    hostTimer -= 1;
    if (hostTimer == 0) global.winners = TEAM_SPECTATOR;
}
//both teams' countdown timers
if (!KothRedControlPoint.locked or !KothBlueControlPoint.locked) {
    if KothBlueControlPoint.team == TEAM_RED && redTimer >0 redTimer-=1;
    if KothRedControlPoint.team == TEAM_BLUE && blueTimer >0 blueTimer-=1;
}
//endgame logic
if (redTimer == 0 and KothBlueControlPoint.team == TEAM_RED and KothBlueControlPoint.capping == 0 and KothBlueControlPoint.bluePresence == 0) global.winners = TEAM_RED;
if (blueTimer == 0 and KothRedControlPoint.team == TEAM_BLUE and KothRedControlPoint.capping == 0 and KothRedControlPoint.redPresence == 0) global.winners = TEAM_BLUE;
if global.isHost and KothRedControlPoint.locked and KothBlueControlPoint.locked and !global.mapchanging {
    if cpUnlock == 0 {
        sendEventUnlockCP();
        doEventUnlockCP();
    }
    else if cpUnlock == 150 {
        GameServer.syncTimer = 1;
    }
}
if cpUnlock > 0 cpUnlock -= 1;
